import React, { Component } from "react";
// import {Route, Switch, Link} from 'react-router-dom';
import './GroupView.css';

class GroupView extends Component{
    state = {
        invalidForm: true,
        formData : {
            name : '',
        }
    };
    formRef = React.createRef();
    handleSubmit = e => {
        e.preventDefault();
        this.props.handleAddCoffee(this.state.formData);
    };
    handleChange = e => {
    const formData = {...this.state.formData, [e.target.name]: e.target.value};
    this.setState({
        formData,
        invalidForm: !this.formRef.current.checkValidity()
    });
    };
    render(){
        return(
            <div>
                <h1>My Group</h1>
                {this.props.user.groups.name
                    ?
                    this.props.user.groups[0].name
                    :
                    <form className='coffeeForm' ref={this.formRef} autoComplete="off" onSubmit={this.handleSubmit}>
                    <div className="col-auto">
                        <label className="sr-only">Coffee</label>
                        <div className="input-group mb-2">
                            <div className="input-group-prepend">
                                <div className="input-group-text">( L )</div>
                            </div>
                            <input className="form-control" 
                            name="size"
                            value={this.state.formData.size}
                            onChange={this.handleChange}
                            required
                             />
                        </div>
                    </div>

                    <button className='addBtn'
                        type='submit'
                        disabled={this.state.invalidForm}
                    >

                        Add
                    </button>


                </form>

                } 


            </div>
        );

    }
}
export default GroupView;
