import React, { Component } from "react";
import userService from "../../utils/userService";
import { Redirect } from "react-router-dom";


export class ProfilePage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isDeleted: false
        };
    }

    // handleChange = evt => {
    //     this.setState({
    //         [evt.target.name]: evt.target.value
    //     });
    // };

    // handleSubmit = async evt => {
    //     evt.preventDefault();
    //     try {
    //         await userService.updateUser(this.state);
    //         this.props.history.push("/profile");
    //     } catch (err) {
    //         console.log(err);
    //     }
    // };

    handleDelete = async evt => {
        evt.preventDefault();
        try {
            console.log(this.props.user)
            await userService.deleteUser(this.props.user);
            this.setState({ isDeleted: true });
            this.props.handleDeleteProfile();
        } catch (err) {
            console.log(err);
        }
    };

    render() {
        if (this.state.isDeleted) {
            return <Redirect to="/" />;
        } else {
            return (
                <div className="ProfilePage">
                     <div className="">
                         <header>
                             <h2 className="basic-title font-weight-bold profile-title">
                                 {this.props.user.name}'s Profile
                             </h2>
                         </header>
                        <br />
                                <h4>Do you want to delete your account ?</h4>
                                <button
                                    className="btn btn-default"
                                    onClick={this.handleDelete}
                                >
                                    Delete Account
                                </button>


                    </div>

                    <br />
                </div>

            );
        }
    }
}

export default ProfilePage;
