import React, { Component } from "react";
// import {Route, Switch, Link} from 'react-router-dom';
import './CoffeeChart.css';
import { VictoryPie, VictoryAnimation, VictoryLabel } from 'victory';

class CoffeeChart extends Component{

    render() {
        return (
            <div className='chart'>
            <svg viewBox="0 0 400 400" width="100%" height="100%">
                <VictoryPie
                standalone={false}
                animate={{ duration: 1000 }}
                width={400} height={400}
                data={this.props.sizedata}
                innerRadius={120}
                cornerRadius={25}
                labels={() => null}
                style={{
                    data: { fill: ({ datum }) => {
                    const color = datum.y > 80 ? "orangered" : "blue";
                    return datum.x === 1 ? color : "transparent";
                    }
                    }
                }}
                />
                <VictoryAnimation duration={1000} data={this.props.sizedata}>
                {(newProps) => {
                    return (
                    <VictoryLabel
                        textAnchor="middle" verticalAnchor="middle"
                        x={200} y={200}
                        text={`${Math.round(this.props.sizepercent)}% 
                        Coffee`}
                        style={{ fontSize: 45 }}
                    />
                    );
                }}
                </VictoryAnimation>
            </svg>
            </div>
        );
    }

}

export default CoffeeChart;
