import React, { Component } from "react";
// import {Route, Switch, Link} from 'react-router-dom';
import './ChatBox.css';
import postService from '../../utils/postService';


class ChatBox extends Component{
    state = {
        posts : [],
        invalidForm: true,
        formData : {
            name : '',
        },
    };
    formRef = React.createRef();
    handleSubmitPost = e => {
        e.preventDefault();
        this.handleAddPost(this.state.formData);
    };
    handleChange = e => {
    const formData = {...this.state.formData, [e.target.name]: e.target.value};
    this.setState({
        formData,
        invalidForm: !this.formRef.current.checkValidity()
    });
    };
    handleAddPost = async newPostData => {
        const posts = await groupService.create(newPostData);
        this.setState({...posts})
    }
    async componentDidMount() {
        const posts = await postService.getAllpost(this.props.lastGroup.id);
        this.setState({posts}); 
    }
    render(){
        return(
            <div className="ChatBox">
                <h1>Chatbox</h1>
                <section id="chat" class="boxshadow">

                </section>
                <section id="user" class="boxshadow">
                    <form>
                        <textarea id="message" class="form-control" handle></textarea>
                        <button type="submit" disabled={this.isFormInvalid()} class="btn">Send</button>
                    </form>

                </section>
            </div>
        )

    }


}

export default ChatBox;
