import React, { Component } from "react";
import './MainDashboard.css';
import coffeeService from '../../utils/coffeeService';
import MainHeader from '../../components/MainHeader/MainHeader';
import Dashboard from '../../components/Dashboard/Dashboard';
import GroupView from '../../components/GroupView/GroupView';


class MainDashboard extends Component{
   state = {
    coffees : [],
    theme : 'light',
    sumSize : 0,
    sumCost : 0,
    percent: 0,
    data: this.getData(0)
   }

   getData(percent) {
    return [{ x: 1, y: percent }, { x: 2, y: 100 - percent }];
  }

  handleAddCoffee = async newCoffeeData => {
    const coffees = await coffeeService.create(newCoffeeData, this.props.user.id);
    this.setState(state => ({
      coffees
    }),
    // Using cb to wait for state to update before rerouting
    () => this.props.history.push('/'));
  }

  async componentDidMount() {
    const coffees = await coffeeService.getAll(this.props.user.id);
    console.log(coffees);
    const sumSize = coffees.reduce(function(prev, cur) {
      return prev + cur.size;
    }, 0);
    const sumCost = coffees.reduce(function(prev, cur){
      return prev + cur.price;
    }, 0);
    const percent = (sumCost / this.props.user.limitOfExpense ) *100;
    this.setState({coffees, sumCost, sumSize, percent});
  }

  async shouldComponentUpdate(){

  }
    render(){
        return(
            <div className='MainDashboard'>
                <div className='flexLeft'>
                <MainHeader user={this.props.user}></MainHeader>
                <Dashboard user={this.props.user} handleAddCoffee={this.handleAddCoffee} percent={this.state.percent} data={this.state.data}>
                </Dashboard>
                </div>
                <div className='flexRight'>
                <GroupView></GroupView>
                </div>


            </div>
        );
    }
}

export default MainDashboard;
